mapper.update:
update t_interview_flow set step=#{step},updater_id=#{updaterId}

	,received = #{received}


	,accepted = #{accepted}


	,arrived_date = #{arrivedDate}


	,refused = #{refused}


	,visa_date = #{visaDate}


	,place = #{place}


	,flight_date = #{flightDate}

 where resume_id=#{resumeId}

mapper.select:
select f.id,f.resume_id,f.step,f.received,f.accepted,f.arrived_date,f.refused,f.visa_date, r.id "resumeInfoPo.id",r.name "resumeInfoPo.name",r.age "resumeInfoPo.age",r.gender "resumeInfoPo.gender", r.position "resumeInfoPo.position",r.phone "resumeInfoPo.phone",r.citizenship "resumeInfoPo.citizenship", r.education "resumeInfoPo.education",r.major "resumeInfoPo.major",r.country "resumeInfoPo.country", r.certification "resumeInfoPo.certification",r.specialized "resumeInfoPo.specialized", r.experience_length "resumeInfoPo.experienceLength",r.other_positions "resumeInfoPo.otherPositions", r.birth_date "resumeInfoPo.birthDate", r.salary "resumeInfoPo.birthDate",r.location "resumeInfoPo.birthDate",r.create_time "resumeInfoPo.createTime"
from t_interview_flow f inner join t_resume_info r on f.resume_id = r.id where step=#{step}
order by #{col} #{order}
limit beginIndex,size
